dw:transform-message.dw:set-variable:
%dw 2.0
%output application/java
---
{
	externalDatabaseName: payload.connection_string match {
	 	/.*(EXTERNAL_DATABASE_NAME=(\w+)).*/ ->  payload.external_database when (payload.external_database != null) otherwise ($[2]),
	 	default -> payload.external_database
	},
	externalColumnName: payload.parameters match {
		/.*Input=(.*)\ \ \ \ .*/ -> payload.external_column_name when (payload.external_column_name != null) otherwise $[1] replace " " with "_",
		default -> payload.external_column_name
	},
	dataQualityRule: payload.description match {
		/.*DATA_QUALITY_RULE=([^;]*).*/ -> $[1],
		default -> null
	},
	dataQualityDimension: payload.description match {
		/.*DIMENSION=([^;]*).*/ -> $[1],
		default -> null
	},
	dataQualityRuleDescription: payload.description match {
		/.*DESCRIPTION=([^;]*).*/ -> $[1],
		default -> null
	}
}

dw:transform-message.dw:set-payload:
%dw 1.0
%output application/json
---
{
	 "externalSystemId": flowVars.externalSystemId,
	 "communityName": flowVars.communityName,
	 "results": [{
		 "externalEntityId": flowVars.values.externalDatabaseName ++ "." ++ payload.external_schema ++ "." ++ payload.external_table_name ++ "." ++ flowVars.values.externalColumnName ++ "." ++ payload.name,
		 "name": payload.name,
		 "dataQualityRuleName": flowVars.values.dataQualityRule,
		 "note": payload.function,
		 "dimension": flowVars.values.dataQualityDimension,	 	 
		 "description": flowVars.values.dataQualityRuleDescription, 
		 "evaluatedColumns": [{
			 "databaseName": flowVars.values.externalDatabaseName,
			 "schemaName": payload.external_schema,
			 "tableName": payload.external_table_name,
			 "name": flowVars.values.externalColumnName
	 	 }],
	 "rowsPassed": payload.rows_passed,
	 "rowsFailed": payload.rows_failed,
	 "qualityScore": payload.score,
	 "result": payload.result match {
	 	"Green" -> true,
	 	default -> false
	 },
	 "threshold": payload.pass_range match {	
	 	/(\d+)%/ -> $[1] as :number,
	 	default -> 0
	 },
	 "customAttributes": [{
		 "typeId": "",
		 "values": [""]
	  }]	
	 }]
 }
